import React, { useState } from "react";

export default function ManagerSignIn({ onLoginSuccess }) {
  const [credentials, setCredentials] = useState({ email: "", password: "" });
  const [error, setError] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    // Simple authentication check
    if (credentials.email === "manager@pokerroom.com" && credentials.password === "manager123") {
      if (onLoginSuccess) {
        onLoginSuccess('manager-dashboard');
      }
    } else {
      setError("Invalid credentials. Use manager@pokerroom.com / manager123");
    }
  };

  const handleChange = (e) => {
    setCredentials({
      ...credentials,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-950 to-black flex items-center justify-center">
      <div className="max-w-md w-full mx-4">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="text-4xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-yellow-400 via-green-300 to-teal-400 mb-4">
            Manager Portal
          </div>
          <p className="text-gray-300 text-lg">Sign in to access manager controls</p>
        </div>

        {/* Login Form */}
        <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-8 shadow-2xl border border-white/20">
          {error && (
            <div className="mb-4 p-3 bg-red-500/20 border border-red-500/50 rounded-lg text-red-300 text-sm">
              {error}
            </div>
          )}

          <form onSubmit={handleSubmit} className="space-y-6">
            <div>
              <label className="block text-white text-sm font-medium mb-2">
                Email Address
              </label>
              <input
                type="email"
                name="email"
                value={credentials.email}
                onChange={handleChange}
                className="w-full px-4 py-3 bg-white/10 border border-white/20 rounded-lg text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-yellow-400 focus:border-transparent"
                placeholder="Enter your email"
                required
              />
            </div>

            <div>
              <label className="block text-white text-sm font-medium mb-2">
                Password
              </label>
              <input
                type="password"
                name="password"
                value={credentials.password}
                onChange={handleChange}
                className="w-full px-4 py-3 bg-white/10 border border-white/20 rounded-lg text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-yellow-400 focus:border-transparent"
                placeholder="Enter your password"
                required
              />
            </div>

            <button
              type="submit"
              className="w-full bg-gradient-to-r from-yellow-500 to-orange-600 hover:from-yellow-400 hover:to-orange-500 text-white font-bold py-3 px-4 rounded-lg shadow-lg transition-all duration-300 transform hover:scale-105"
            >
              Sign In to Manager Portal
            </button>
          </form>

          {/* Demo Credentials */}
          <div className="mt-6 p-4 bg-blue-500/20 border border-blue-500/30 rounded-lg">
            <div className="text-blue-300 text-sm font-medium mb-2">Demo Credentials:</div>
            <div className="text-blue-200 text-sm">
              <div>Email: manager@pokerroom.com</div>
              <div>Password: manager123</div>
            </div>
          </div>

          {/* Back to Main Dashboard */}
          <div className="mt-6 text-center">
            <button
              onClick={() => onLoginSuccess && onLoginSuccess('main')}
              className="text-gray-400 hover:text-white text-sm transition-colors"
            >
              ← Back to Main Dashboard
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
