import React, { useState } from 'react';
import './index.css';
import FnbDashboard from './components/FnbDashboard';
import DashboardPage from './components/DashboardPage';
import GreDashboard from './components/GreDashboard';
import GreSignIn from './components/GreSignIn';
import HrDashboard from './components/HrDashboard';
import HrSignIn from './components/HrSignIn';
import ManagerSignIn from './components/ManagerSignIn';
import ManagerDashboard from './components/ManagerDashboard';

function App() {
  const [currentView, setCurrentView] = useState('fnb-dashboard');
  const [isAuthenticated, setIsAuthenticated] = useState(true);

  const renderCurrentView = () => {
    if (!isAuthenticated) {
      return (
        <div className="min-h-screen bg-gray-100 flex items-center justify-center">
          <div className="max-w-md w-full space-y-8">
            <div>
              <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900">
                Staff Portal Login
              </h2>
              <p className="mt-2 text-center text-sm text-gray-600">
                Choose your department
              </p>
            </div>
            <div className="space-y-4">
              <button
                onClick={() => setIsAuthenticated(true)}
                className="group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
              >
                FNB Manager Portal
              </button>
              <button
                onClick={() => setIsAuthenticated(true)}
                className="group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500"
              >
                GRE Dashboard
              </button>
              <button
                onClick={() => setIsAuthenticated(true)}
                className="group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-purple-600 hover:bg-purple-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-purple-500"
              >
                HR Dashboard
              </button>
              <button
                onClick={() => setCurrentView('manager-signin')}
                className="group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-orange-600 hover:bg-orange-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-orange-500"
              >
                Manager Portal
              </button>
            </div>
          </div>
        </div>
      );
    }

    switch (currentView) {
      case 'fnb-dashboard':
        return <FnbDashboard />;
      case 'gre-dashboard':
        return <GreDashboard />;
      case 'hr-dashboard':
        return <HrDashboard />;
      case 'gre-signin':
        return <GreSignIn />;
      case 'hr-signin':
        return <HrSignIn />;
      case 'manager-signin':
        return <ManagerSignIn onLoginSuccess={setCurrentView} />;
      case 'manager-dashboard':
        return <ManagerDashboard onNavigate={setCurrentView} />;
      default:
        return (
          <div className="min-h-screen bg-gray-100 flex items-center justify-center">
            <div className="max-w-4xl w-full">
              <div className="bg-white rounded-lg shadow-lg p-8">
                <h1 className="text-3xl font-bold text-center text-gray-900 mb-8">
                  Staff Portal Dashboard
                </h1>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                  <div className="bg-blue-50 rounded-lg p-6 text-center">
                    <div className="text-4xl mb-4">üçΩÔ∏è</div>
                    <h2 className="text-xl font-semibold text-gray-900 mb-2">FNB Manager Portal</h2>
                    <p className="text-gray-600 mb-4">Manage menu, inventory, orders, and reports</p>
                    <button
                      onClick={() => setCurrentView('fnb-dashboard')}
                      className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700"
                    >
                      Access Portal
                    </button>
                  </div>

                  <div className="bg-green-50 rounded-lg p-6 text-center">
                    <div className="text-4xl mb-4">üë•</div>
                    <h2 className="text-xl font-semibold text-gray-900 mb-2">GRE Dashboard</h2>
                    <p className="text-gray-600 mb-4">Guest Relations Executive tools</p>
                    <button
                      onClick={() => setCurrentView('gre-dashboard')}
                      className="bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700"
                    >
                      Access Dashboard
                    </button>
                  </div>

                  <div className="bg-purple-50 rounded-lg p-6 text-center">
                    <div className="text-4xl mb-4">üë®‚Äçüíº</div>
                    <h2 className="text-xl font-semibold text-gray-900 mb-2">HR Dashboard</h2>
                    <p className="text-gray-600 mb-4">Human Resources management</p>
                    <button
                      onClick={() => setCurrentView('hr-dashboard')}
                      className="bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700"
                    >
                      Access Dashboard
                    </button>
                  </div>

                  <div className="bg-orange-50 rounded-lg p-6 text-center">
                    <div className="text-4xl mb-4">üéØ</div>
                    <h2 className="text-xl font-semibold text-gray-900 mb-2">Manager Portal</h2>
                    <p className="text-gray-600 mb-4">Management and analytics</p>
                    <button
                      onClick={() => setCurrentView('manager-signin')}
                      className="bg-orange-600 text-white px-4 py-2 rounded-lg hover:bg-orange-700"
                    >
                      Access Portal
                    </button>
                  </div>

                  <div className="bg-red-50 rounded-lg p-6 text-center">
                    <div className="text-4xl mb-4">‚ö°</div>
                    <h2 className="text-xl font-semibold text-gray-900 mb-2">Super Admin Portal</h2>
                    <p className="text-gray-600 mb-4">Ultimate system control</p>
                    <button
                      onClick={() => setCurrentView('superadmin-signin')}
                      className="bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700"
                    >
                      Access Portal
                    </button>
                  </div>
                </div>

                <div className="mt-8 text-center">
                  <button
                    onClick={() => setIsAuthenticated(false)}
                    className="text-gray-500 hover:text-gray-700"
                  >
                    Switch User / Logout
                  </button>
                </div>
              </div>
            </div>
          </div>
        );
    }
  };

  return (
    <div className="App">
      {renderCurrentView()}
    </div>
  );
}

export default App;
